changeSet.sql:
update commit set repository_id = (select id from repository where repository.repository_name = commit.repository_name);
update commit_comment set repository_id = (select id from repository where repository.repository_name = commit_comment.repository_name);
update group_warning set repository_id = (select id from repository where repository.repository_name = group_warning.repository_name);
update pull_requests set repository_id = (select id from repository where repository.repository_name = pull_requests.repository_name);
update pull_request_comments set repository_id = (select id from repository where repository.repository_name = pull_request_comments.repository_name);
